import pandas as pdimport numpy as npimport matplotlib.pyplot as plt# from sklearndf = pd.read_csv('Age_wgt.csv')print(df)K = 3rand_cent = df.sample(n = K)print(rand_cent)print(type(rand_cent))print()centroids = rand_cent.values.tolist()c1 = centroids[0]c2 = centroids[1]c3 = centroids[2]allpoints = df.values.tolist()print(c1, c2, c3)cl1 = []cl2 = []cl3 = []C1 = []C2 = []C3 = []while (True):    for i in range(len(allpoints)):        a = c1        b = c2        c = c3        d1 = np.sqrt((allpoints[i][0] - c1[0]) ** 2 + (allpoints[i][1] - c1[1]) ** 2)        d2 = np.sqrt((allpoints[i][0] - c2[0]) ** 2 + (allpoints[i][1] - c2[1]) ** 2)        d3 = np.sqrt((allpoints[i][0] - c3[0]) ** 2 + (allpoints[i][1] - c3[1]) ** 2)        smd = min(min(d1, d2), d3)        if (smd == d1):            C1.append(allpoints[i])        if (smd == d2):            C2.append(allpoints[i])        if (smd == d3):            C3.append(allpoints[i])        if (len(C1) != 0):            cc1 = [sum(x) for x in zip(*C1)]            c1[0] = (c1[0] + cc1[0]) / (len(C1) + 1)            c1[1] = (c1[1] + cc1[1]) / (len(C1) + 1)        if (len(C2) != 0):            cc2 = [sum(x) for x in zip(*C2)]            c2[0] = (c2[0] + cc2[0]) / (len(C2) + 1)            c2[1] = (c2[1] + cc2[1]) / (len(C2) + 1)        if (len(C3) != 0):            cc3 = [sum(x) for x in zip( * C3)]            c3[0] = (c3[0] + cc3[0]) / (len(C3) + 1)            c3[1] = (c3[1] + cc3[1]) / (len(C3) + 1)    if (a == c1 and b == c2 and c == c3):        breakprint("\n\nCluster 1", "\n")print(C1, "\n\n")print("Cluster 2", "\n\n")print(C2, "\n\n")print("Cluster 3", "\n\n")print(C3, "\n\n")col = ['red', 'blue', 'yellow']markers = ['o', 'v', 's']for i in range(len(C1)):    plt.plot(C1[i][0], C1[i][1], color = col[0], marker = markers[0])for i in range(len(C2)):    plt.plot(C2[i][0], C2[i][1], color = col[1], marker = markers[1])for i in range(len(C3)):    plt.plot(C3[i][0], C3[i][1], color = col[2], marker = markers[2])plt.plot(c1[0], c1[1], color = 'green', marker = 'D', markersize = 7)plt.plot(c2[0], c2[1], color = 'green', marker = 'D', markersize = 7)plt.plot(c3[0], c3[1], color = 'green', marker = 'D', markersize = 7)plt.show()